&lt;span class=&#34;hljs-function&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;void&lt;/span&gt; &lt;span class=&#34;hljs-title&#34;&gt;dfs&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; x, &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; first, &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; second)&lt;/span&gt; &lt;/span&gt;&amp;#123;
    &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt;(u[x].empty()) &amp;#123;
        &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt;(ans[x].first &amp;amp;&amp;amp; ans[x].second) &amp;#123;
            &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt;(ans[x].second == second) &amp;#123;
                ans[x].first += first;
                &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; tmp = gcd(ans[x].first, ans[x].second);
                ans[x].first /= tmp;
                ans[x].second /= tmp;
            &amp;#125;
            &lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt; &amp;#123;
                first *= ans[x].second;
                ans[x].first *= second;
                ans[x].first += first;
                ans[x].second *= second;
                &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;long&lt;/span&gt; tmp = gcd(ans[x].first, ans[x].second);
                ans[x].first /= tmp;
                ans[x].second /= tmp;
            &amp;#125;
        &amp;#125;
        &lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt; &amp;#123;
            ans[x].first = first;
            ans[x].second = second;
        &amp;#125;
        &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt;;
    &amp;#125;
    &lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; s = u[x].size();
    second *= s;
    &lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt; ; i &amp;lt; s; i++) &amp;#123;
        dfs(u[x][i], first, second);
    &amp;#125;
&amp;#125;

&lt;span class=&#34;hljs-function&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; &lt;span class=&#34;hljs-title&#34;&gt;main&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;()&lt;/span&gt; &lt;/span&gt;&amp;#123;
    &lt;span class=&#34;hljs-built_in&#34;&gt;cin&lt;/span&gt; &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
    &lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt; ; i &amp;lt;= n ; i++) &amp;#123;
        &lt;span class=&#34;hljs-built_in&#34;&gt;cin&lt;/span&gt; &amp;gt;&amp;gt; d;
        &lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; j = &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt; ; j &amp;lt;= d ; j++) &amp;#123;
            &lt;span class=&#34;hljs-built_in&#34;&gt;cin&lt;/span&gt; &amp;gt;&amp;gt; t;
