    &lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt; ; i &amp;lt;= m ; i++) &amp;#123;
        dfs(i, &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;);
    &amp;#125;
    &lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt; ; i &amp;lt;= n ; i++) &amp;#123;
        &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt;(u[i].empty()) &amp;#123;
            &lt;span class=&#34;hljs-built_in&#34;&gt;cout&lt;/span&gt; &amp;lt;&amp;lt; ans[i].first &amp;lt;&amp;lt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27; &amp;#x27;&lt;/span&gt; &amp;lt;&amp;lt; ans[i].second &amp;lt;&amp;lt; &lt;span class=&#34;hljs-built_in&#34;&gt;endl&lt;/span&gt;;
        &amp;#125;
    &amp;#125;
    &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;hljs-number&#34;&gt;0&lt;/span&gt;;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
