&lt;span class=&#34;hljs-function&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;void&lt;/span&gt; &lt;span class=&#34;hljs-title&#34;&gt;dfs&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; x, __int128 first, __int128 second)&lt;/span&gt; &lt;/span&gt;&amp;#123;
    &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (u[x].empty()) &amp;#123;
        &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (ans[x].first &amp;amp;&amp;amp; ans[x].second) &amp;#123;
            &lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt; (ans[x].second == second) &amp;#123;
                ans[x].first += first;
                __int128 tmp = gcd(ans[x].first, ans[x].second);
                ans[x].first /= tmp;
                ans[x].second /= tmp;
            &amp;#125;
            &lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt; &amp;#123;
                first *= ans[x].second;
                ans[x].first *= second;
                ans[x].first += first;
                ans[x].second *= second;
                __int128 tmp = gcd(ans[x].first, ans[x].second);
                ans[x].first /= tmp;
                ans[x].second /= tmp;
            &amp;#125;
        &amp;#125;
        &lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt; &amp;#123;
            ans[x].first = first;
            ans[x].second = second;
        &amp;#125;
        &lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt;;
